import { Component,Input } from '@angular/core';
import { Rapport } from '../models/rapport.model';
import { Router } from '@angular/router';
import { Rapportservice } from '../services/rapport.service';
import { Observable } from 'rxjs';

@Component({
  selector: 'app-rapport',
  templateUrl: './rapport.component.html',
  styleUrls: ['./rapport.component.scss']
})
export class RapportComponent {
  @Input() rapport!:Rapport;

rapport$!:Observable<Rapport[]>;

  constructor(private route:Router, private rapportService:Rapportservice) {}

  ngOnInit(): void {

    this.rapport$ = this.rapportService.getVsisteurNomPrenomById();


  }
}
<h2>Rapport: {{rapport.id}}</h2>
<p>Date: {{rapport.date}}</p>
<h3>Motif: {{rapport.motif}}</h3>
<h3>Bilan: {{rapport.bilan}}</h3>
<h3 *ngIf="!rapport.idVisiteur">Visiteur: {{rapport.idVisiteur}}</h3>
<h3 *ngIf="rapport.idVisiteur">Visiteur: {{rapport.idVisiteur}}</h3>
