import { Component } from '@angular/core';
import { AuthService } from '../services/auth.service';

@Component({
  selector: 'app-header',
  templateUrl: './header.component.html',
  styleUrls: ['./header.component.scss']
})
export class HeaderComponent {

  titreBoutonConnexion!:string;

  constructor(private authService:AuthService) { }

 getStatusAuth(){
  return this.authService.getAthuStatus();
}

}
<header>
    <nav>
    <a routerLink="/medicament" routerLinkActive="active"
    [routerLinkActiveOptions]="{ exact: true }">Médicament</a>
    <a routerLink="/medecin" routerLinkActive="active"
    [routerLinkActiveOptions]="{ exact: true }">Medecin</a>
    <a routerLink="" routerLinkActive="active" 
    [routerLinkActiveOptions]="{ exact: true }">{{ getStatusAuth() }}</a>
    </nav>
</header>
