import { Injectable } from "@angular/core";
import { HttpClient } from "@angular/common/http";
import { Observable } from 'rxjs';
import { Rapport } from "../models/rapport.model";


@Injectable({
    providedIn:'root'
})

export class Rapportservice {
    constructor(private http: HttpClient) { }
     
    getRapportById(id: number): Observable<Rapport[]> {
        return this.http.get<Rapport[]>(`http://172.20.120.31/gsb/gsbapi/?idRapport=${id}`);
    }
    
    insertRapportById(date: string, motif: string, bilan: string, idVisiteur: string, idMedecin: string): Observable<any> {
        const rapportData = {
            date: date,
            motif: motif,
            bilan: bilan,
            idVisiteur: idVisiteur,
            idMedecin: idMedecin
        };
        return this.http.post<Rapport[]>('http://172.20.120.31/gsb/gsbapi/', rapportData);
    }
}
