import { Component, OnInit } from '@angular/core';
import { MedecinServices } from '../services/medecin.service';
import { Medecin } from '../models/medecin.model';
import { ActivatedRoute } from '@angular/router';

@Component({
  selector: 'app-medecin-seul',
  templateUrl: './medecin-seul.component.html',
  styleUrls: ['./medecin-seul.component.scss']
})
export class MedecSeulComponent implements OnInit{
  unMedicament!:Medicament;

  constructor(private medicamentService:MedicamentServices, private route: ActivatedRoute) { }

  ngOnInit(): void {
    const id: string = this.route.snapshot.params['id'];
    this.unMedicament = this.medicamentService.getMedicamentById(id);
  }

}
