import { Component,Input } from '@angular/core';
import { Rapport } from '../models/rapport.model';
import { Router } from '@angular/router';
import { Rapportservice } from '../services/rapport.service';
import { Observable } from 'rxjs';
import { map } from 'rxjs/operators';

@Component({
  selector: 'app-rapport',
  templateUrl: './rapport.component.html',
  styleUrls: ['./rapport.component.scss']
})
export class RapportComponent{
  @Input() rapport!:Rapport;
  rapport$!:Observable<Rapport[]>;
  unVisiteur$!:Observable<any>;

  constructor(private rapportService:Rapportservice) {}

  ngOnInit(): void {

    this.rapport$ = this.rapportService.getVisiteurNomPrenomById(this.rapport.idVisiteur);


  }

  getNomVisiteur(idVisiteur: string):string {

    this.unVisiteur$ = this.rapportService.getVisiteurNomPrenomById(idVisiteur);
    return this.unVisiteur$.pipe(
      map(visiteur => `${visiteur.nom} ${visiteur.prenom}`)
    )


  }

}
<h2>Rapport: {{rapport.id}}</h2>
<p>Date: {{rapport.date}}</p>
<h3>Motif: {{rapport.motif}}</h3>
<h3>Bilan: {{rapport.bilan}}</h3>
<h3>Visiteur: {{rapport.idVisiteur}}</h3>
<h3>Visiteur: {{ getNomVisiteur(rapport.id) }}</h3>
