import { Component, OnInit } from '@angular/core';
import { MedecinServices } from '../services/medecin.service';
import { Medecin } from '../models/medecin.model';
import { ActivatedRoute } from '@angular/router';

@Component({
  selector: 'app-medecin-seul',
  templateUrl: './medecin-seul.component.html',
  styleUrls: ['./medecin-seul.component.scss']
})
export class MedecinSeulComponent implements OnInit{
  unMedecin!:Medecin;

  constructor(private medecinService:MedecinServices, private route: ActivatedRoute) { }

  ngOnInit(): void {
    const nom: string = this.route.snapshot.params['noms'];
    this.unMedecin = this.medecinService.getMedecinByName(nom);


  }

  getAllMedecin():Observable<Medecin[]>{
    return this.http.get<Medecin[]>('http://172.20.118.4/gsbapi/?noms');
  }

  getMedecinById(id: number):Observable<Medecin> {
    return this.http.get<Medecin>('http://172.20.118.4/gsbapi/?id=4');
  }

}
