import { Component } from '@angular/core';
import { AuthService } from '../services/auth.service';

@Component({
  selector: 'app-header',
  templateUrl: './header.component.html',
  styleUrls: ['./header.component.scss']
})
export class HeaderComponent {

  titreBoutonConnexion!:string;

  constructor(private authService:AuthService) { }

 getStatusAuth(){
  return this.authService.getAthuStatus();
}

}
<header>
    <nav>
        <div class="container">
            <ul class="navbar-nav">
                <li class="nav-item">  
                  <li> <a class="nav-link"><img  src="../../../img/gsb1.png" class="img-rounded" alt="LOGO GSB" width="45" height="20"/></a> </li>
                     <li>
                        <a routerLink="/medicament-list" routerLinkActive="active"
                        [routerLinkActiveOptions]="{ exact: true }">Médicament</a>
                    </li> 
                    <li>
                        <a routerLink="" routerLinkActive="active" 
                        [routerLinkActiveOptions]="{ exact: true }">{{ getStatusAuth() }}</a>
                    </li>


            </ul>

    </div>

    </nav>
</header>
