import { Component,OnInit } from '@angular/core';
import { Rapport } from '../models/rapport.model';
import { ActivatedRoute } from '@angular/router';
import { Rapportservice } from '../services/rapport.service';
import { MedecinServices } from '../services/medecin.service';
import { Medecin } from '../models/medecin.model';
import { Observable } from 'rxjs';

@Component({
  selector: 'app-ajout-rapport',
  templateUrl: './ajout-rapport.component.html',
  styleUrls: ['./ajout-rapport.component.scss']
})
export class AjoutRapportComponent implements OnInit{
  unMedecin$!:Observable<Medecin[]>;
  unRapport$!:Observable<Rapport[]>;
  date!: string;
  motif!: string;
  bilan!: string;
  idVisiteur!: string;
  idMedecin!: string;

  constructor(private medecinService:MedecinServices, private route: ActivatedRoute, private rapportService:Rapportservice) {}

  ngOnInit(): void {
    const id =+ this.route.snapshot.params['id'];
    this.unMedecin$ = this.medecinService.getMedecinById(+id);

    const idRapport = +this.route.snapshot.params['id'];
    this.unRapport$ = this.rapportService.getRapportById(+idRapport);
  }

  onSubmit() {
    console.log('Date :',this.date);
    console.log('Motif :',this.motif);
    console.log('Bilan :',this.bilan);
    console.log('ID du Visiteur :',this.idVisiteur);
  }

}
<div (ngSubmit)="onSubmit()">
    <h2>Ajout de Rapport</h2>
    <div class="col-lg-4 offset-md-4 mb-3 mt-3">
        <input type="text" class="form-control" id="date" placeholder="Saisir la date" name="date" required="required" [()]>
        <input type="text" class="form-control" id="motif" placeholder="Saisir votre motif" name="motif" required="required">
        <input type="text" class="form-control" id="bilan" placeholder="Saisir le bilan" name="bilan" required="required">
        <input type="text" class="form-control" id="visiteur" placeholder="Saisir le ID du visiteur" name="visiteur" required="required">
    </div>
    <button class="btn-valider" type="submit">Valider</button>
</div>
