import { Component, Input } from '@angular/core';
import { Medicament } from '../models/gsb.model';
import { ActivatedRoute, Router } from '@angular/router';
import { MedicamentServices } from '../services/gsb.service';

@Component({
  selector: 'app-medicament',
  templateUrl: './medicament.component.html',
  styleUrls: ['./medicament.component.scss']
})
export class MedicamentComponent {

  @Input() medicament!:Medicament;

  constructor(private medicamentServices:MedicamentServices, private route:Router) { }

  onViewMedicament() {
    this.route.navigateByUrl(`?nomMed=${this.medicament.id}`);
  }
}
