import { Component, OnInit } from '@angular/core';
import { MedecinServices } from '../services/medecin.service';
import { Medecin } from '../models/medecin.model';
import { ActivatedRoute } from '@angular/router';
import { Observable } from 'rxjs';
import { MedecinComponent } from '../medecin/medecin.component';

@Component({
  selector: 'app-medecin-seul',
  templateUrl: './medecin-seul.component.html',
  styleUrls: ['./medecin-seul.component.scss']
})
export class MedecinSeulComponent implements OnInit{
  unMedecin$!:Observable<Medecin>;
  details!:MedecinComponent;

  constructor(private medecinService:MedecinServices, private route: ActivatedRoute, private detail:MedecinServices) { }

  ngOnInit(): void {
    const id =+ this.route.snapshot.params['id'];
    this.unMedecin$ = this.medecinService.getMedecinById(+id);

  }

  DetailsMedecin() {
    this.details = this.detail.getDetailsMedecin();
  }
}
<h2>{{ unMedecin$ | async }}</h2>
<p>{{ unMedecin$}}</p>
<button routerLink="/medecin">Retour à la liste</button>
