import { Injectable } from "@angular/core";
import { HttpClient } from "@angular/common/http";
import { Observable } from 'rxjs';
import { Rapport } from "../models/rapport.model";
import { Visiteur } from "../models/visiteur.model";


@Injectable({
    providedIn:'root'
})

export class Rapportservice {
    visiteurs!:Visiteur[];
    constructor(private http: HttpClient) { }
     
    getRapportById(id: number): Observable<Rapport[]> {
        return this.http.get<Rapport[]>(`http://172.20.120.31/gsb/gsbapi/?idRapport=${id}`);
    }
    
    insertRapportById(date: string, motif: string, bilan: string, idVisiteur: string, idMedecin: string): Observable<any> {

        return this.http.get<Rapport[]>(`http://172.20.120.31/gsb/gsbapi/?addRapport&date=${date}&motif=${motif}&bilan=${bilan}&idVisiteur=${idVisiteur}&idMedecin=${idMedecin}`);
    }
    getVsisteurNomPrenomById():Observable<Visiteur[]>{
        return this.http.get<Visiteur[]>('http://172.20.120.31/gsb/gsbapi/');
      }
}
