import { Component,OnInit } from '@angular/core';
import { Rapport } from '../models/rapport.model';
import { ActivatedRoute } from '@angular/router';
import { Rapportservice } from '../services/rapport.service';
import { MedecinServices } from '../services/medecin.service';
import { Medecin } from '../models/medecin.model';
import { Observable } from 'rxjs';

@Component({
  selector: 'app-supp-rapport',
  templateUrl: './supp-rapport.component.html',
  styleUrls: ['./supp-rapport.component.scss']
})
export class SuppRapportComponent implements OnInit {
  unMedecin$!:Observable<Medecin[]>;
  unRapport$!:Observable<Rapport[]>;
  idRapoort!:string;
  messageConfirmation: string = '';

  constructor(private route: ActivatedRoute, private rapportService: Rapportservice) {}


  ngOnInit(): void {

    const idRapport = +this.route.snapshot.params['i'];
    this.unRapport$ = this.rapportService.getRapportById(+idRapport);
  }

  onDelete() {
    const idRapport: string = this.route.snapshot.params['idRapport'];
    this.unRapport$.subscribe(rapport => {
        console.log(rapport); // Assurez-vous que les données du rapport sont correctement récupérées
        // Maintenant, vous pouvez accéder aux propriétés du rapport et les utiliser pour la suppression
        this.rapportService.deleteRapportById(
            idRapport
        ).subscribe(response => {
            console.log('Rapport supprimé avec succès', response);
            this.messageConfirmation = 'Le rapport a bien été supprimé.';
        }, error => {
            console.error('Erreur lors de la suppression du rapport', error)
            this.messageConfirmation = 'Une erreur est survenue, le rapport n\'a pas été supprimé.';
        });
    });
  setTimeout( 
    () => { 
      this.messageConfirmation = ""; 
    }, 2000 
  );
  }
}
<div>
    <button class="btn-valider" (click)="onDelete()">Supprimer</button>
    <p *ngIf="messageConfirmation" class="message">{{ messageConfirmation }}</p>
</div>
