import { Component,OnInit } from '@angular/core';
import { Rapport } from '../models/rapport.model';
import { ActivatedRoute } from '@angular/router';
import { Rapportservice } from '../services/rapport.service';
import { MedecinServices } from '../services/medecin.service';
import { Medecin } from '../models/medecin.model';
import { Observable } from 'rxjs';

@Component({
  selector: 'app-supp-rapport',
  templateUrl: './supp-rapport.component.html',
  styleUrls: ['./supp-rapport.component.scss']
})
export class SuppRapportComponent implements OnInit {
  unMedecin$!:Observable<Medecin[]>;
  unRapport$!:Observable<Rapport[]>;
  idRapoort!:string;
  date!: string;
  motif!: string;
  bilan!: string;
  idVisiteur!: string;
  id!: string;
  messageConfirmation: string = '';

  constructor(private medecinService: MedecinServices, private route: ActivatedRoute, private rapportService: Rapportservice) {}


  ngOnInit(): void {
    const id =+ this.route.snapshot.params['id'];
    this.unMedecin$ = this.medecinService.getMedecinById(+id);

    const idRapport = +this.route.snapshot.params['id'];
    this.unRapport$ = this.rapportService.getRapportById(+idRapport);
  }

  onDelete() {
     const idRapport: string =this.route.snapshot.params['id'];
    console.log(idRapport);
    console.log(this.date);
    console.log(this.motif);
    console.log(this.bilan);
    console.log(this.idVisiteur);
    const idMedecin: string = this.route.snapshot.params['idMedecin'];
    console.log(idMedecin);

    this.rapportService.deleteRapport(
      idRapport,
      this.date,
      this.motif,
      this.bilan,
      this.idVisiteur,
      idMedecin
    ).subscribe(response => {
      console.log('Rapport supprimer avec succès', response);
      this.messageConfirmation = 'Le rapport a bien été supprimer.';
  }, error => {
      console.error('Erreur lors de la suppression du rapport', error)
      this.messageConfirmation = 'Une erreur est survenue, le rapport n\'a pas été supprimer.';
  });
  setTimeout( 
    () => { 
      this.messageConfirmation = ""; 
    }, 2000 
  );
  }
}
<div>
    <button class="btn-valider" (click)="onDelete()">Supprimer</button>
    <p *ngIf="messageConfirmation" class="message">{{ messageConfirmation }}</p></div>
